<?php //arrays para decodificar claves de deportes y provincias$clavesDeportes = array(	'F' => 'Fútbol' ,	'T' => 'Tenis' ,	'C' => 'Curling' ,	'B' => 'Baloncesto');$clavesProvincias = array(	'CO' => 'A Coruña' ,	'LU' => 'Lugo' ,	'OU' => 'Ourense' ,	'PO' => 'Pontevedra');$op=$_GET["op"] ?? "";$buscar=$_GET["buscar"] ?? "";$nreg=$_GET["nreg"] ?? "";$c=conectarBaseDatos(); //conectamos con servidor base datos.if($op=="logout") {	$_SESSION=[];//destruir todas las variables de sesión (eliminamos sesión)	echo "<script>document.location.href='index.php'</script>"; //esto nos redirige a index.php , sin parámetros	die();}if($op=="log"){	require "login.php";}if(!isset($_SESSION['email']))	{ // sino se inició sesión	return;}	if($op=="a" || $op=="m") { //opciónes de añadir o de modificar	require "formulario.php";	return;}//listo los registros de la tabla alumnos2if($buscar) {	//construir condición	$cadenasBuscar=explode(" ", $buscar);	$condicion="0"; // 0 equivale a false	foreach ($cadenasBuscar as $key => $value) {		$condicion.=" OR nombre LIKE '%$value%' OR apellidos LIKE '%$value%'";	}	//$condicion="nombre LIKE '%$buscar%' OR apellidos LIKE '%$buscar%'";	echo "<hr><b>$condicion</b><hr>";} else	$condicion=1;$sql="SELECT * FROM alumnos2 WHERE $condicion ORDER BY apellidos, nombre";$result=enviarConsulta($c,$sql);?><table class="table table-bordered talbe-striped">	<tr>		<th>Apellidos</th>		<th>Nombre</th>		<th class="text-center">Sexo</th>		<th class="text-center">NIF</th>		<th class="text-center">Deportes</th>		<th class="text-center">Provincia</th>		<th class="text-center">Acciones</th>	</tr><?php while ($fila=mysqli_fetch_array($result)) {	echo "\t<tr>\n";	echo "\t\t<td>".$fila['apellidos']."</td>\n";	echo "\t\t<td>".$fila['nombre']."</td>\n";	echo "\t\t<td class='text-center'>".$fila['sexo']."</td>\n";	echo "\t\t<td class='text-center'>".$fila['nif']."</td>\n";	echo "\t\t<td class='text-center'>";	$deportes=explode("-", $fila['deportes']);	foreach ($deportes as $claveDeporte) {		echo "$clavesDeportes[$claveDeporte]<br>";	}	echo "</td>\n";		echo "\t\t<td class='text-center'>".$clavesProvincias[$fila['provincia']]."</td>\n";	echo "\t\t<td class='text-center'>";	echo "<a href='index.php?op=m&nreg={$fila['registro']}' class='btn btn-success' >Modificar</a> ";	echo "<a href='#' onclick='estasSeguro({$fila['registro']},\"{$fila['nombre']}\",\"{$fila['apellidos']}\")' class='btn btn-danger'>Eliminar</a>";	echo "</td>\n";	}mysqli_close($c);?>	</table>